from Crypto.Util.number import long_to_bytes, inverse
import random

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def factor_n_given_d(N, e, d):
    k = e * d - 1
    t = 0
    r = k
    while r % 2 == 0:
        t += 1
        r //= 2
    for _ in range(100):
        g = random.randint(2, N - 1)
        y = pow(g, r, N)
        if y == 1 or y == N - 1:
            continue
        for _ in range(t - 1):
            x = pow(y, 2, N)
            if x == 1:
                p = gcd(y - 1, N)
                if 1 < p < N:
                    q = N // p
                    return p, q
            if x == N - 1:
                break
            y = x
    raise ValueError("Failed to factor N")

